import React, { useState, Fragment } from "react";
import PropTypes from "prop-types";
import { Flex, Heading, Box, Link, Text } from "rebass";
import { loggedOutLinks, loggedInLinks } from "./links";
import MenuContainer from "../../components/Menu/MenuContainer";
import Media from "react-media";

const Header = ({ isUser }) => {
  const links = isUser ? loggedInLinks : loggedOutLinks;
  const [isOpen, setIsOpen] = useState(false);
  const [paddingHeader, setPaddingHeader] = useState();

  return (
    <div>
      <Flex
        alignItems="center"
        px={paddingHeader}
        py={3}
        sx={{
          boxShadow: "0 2px 4px 0 rgba(0,0,0,.1)",
          height: "70px",
          background: "#f13c20",
          borderBottom: "2px solid white",
          position: "fixed",
          top: 0,
          width: "100%",
        }}
      >
        <Heading>
          <Text
            fontSize={4}
            fontWeight="900"
            color="white"
            sx={{
              cursor: "pointer",
            }}
          >
            Home
          </Text>
        </Heading>

        
      </Flex>
      <MenuContainer isOpen={isOpen} links={links} />
    </div>
  );
};

Header.propTypes = {
  isUser: PropTypes.bool.isRequired,
};

export default Header;
