import React, { useState, Fragment } from "react";
import PropTypes from "prop-types";
import { Flex, Heading, Box, Link, Text } from "rebass";
import { loggedOutLinks, loggedInLinks } from "./links";
import { useMediaQuery } from "react-responsive";

const Header = ({ isUser }) => {
  const links = isUser ? loggedInLinks : loggedOutLinks;
  const [isOpen, setIsOpen] = useState(false);
  const [paddingHeader, setPaddingHeader] = useState();

  const isMobile = useMediaQuery({
    query: "(max-device-width: 768px)",
  });
  const isLaptop = useMediaQuery({
    query: "(min-device-width: 769px)",
  });

  return (
    <div>
      <Flex
        px={paddingHeader}
        py={3}
        color="white"
        bg="black"
        alignItems="center"
      >
        <Text p={2} fontWeight="bold">
          Home
        </Text>
        <Box mx="auto" />
        {isLaptop && (
          <>
            {setPaddingHeader(6)}
            {links.right.map((link) => (
              <Link id={link.id}>{link.id}</Link>
            ))}
          </>
        )}
        {isMobile && (
          <>
            <Box
              sx={{ cursor: "pointer" }}
              onClick={() => {
                setIsOpen(!isOpen);
              }}
            >
              {setPaddingHeader(4)}
              {isOpen ? (
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="24"
                  height="24"
                  viewBox="0 0 18 18"
                  fill="white"
                >
                  <path d="M14.53 4.53l-1.06-1.06L9 7.94 4.53 3.47 3.47 4.53 7.94 9l-4.47 4.47 1.06 1.06L9 10.06l4.47 4.47 1.06-1.06L10.06 9z" />
                </svg>
              ) : (
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="24"
                  height="24"
                  viewBox="0 0 18 18"
                  fill="white"
                >
                  <path d="M2 13.5h14V12H2v1.5zm0-4h14V8H2v1.5zM2 4v1.5h14V4H2z" />
                </svg>
              )}
            </Box>
          </>
        )}
      </Flex>
      <div>menu tings</div>
    </div>
  );
};

Header.propTypes = {
  isUser: PropTypes.bool.isRequired,
};

export default Header;
