import React from "react";
import PropTypes from "prop-types";

import {Link} from "rebass"
import { Input } from "@rebass/forms";

import registerIcon from "../../assets/icons/face-black-18dp.svg";


const DropMenuContent = ({ isUser }) => {
  if (isUser) {
    return (
      <div
        style={{
          display: "flex",
          border: "1px solid red",
          textAlign: "center",
          color: "black",
          flexDirection: "column",
        }}
      >
        <a>Link 1</a>
        <a>Link 2</a>
      </div>
    );
  } else {
    return (
      <div
        style={{
          display: "flex",
          textAlign: "center",
          flexDirection: "column",
          border: "1px solid red",
          color: "black",
        }}
      >
        <div
          style={{
            display: "inline-block",
          }}
        >
          <Input
            id="searchBox"
            fontSize={1}
            onMouseOver={() => {
              setDropMenuStatus(true);
            }}
            onMouseOut={() => {
              setDropMenuStatus(false);
            }}
            sx={{
              minWidth: "0",
              maxWidth: "0",
              width: "0",
              textDecoration: "none",
              outline: "none",
              borderBottom: "none",
              borderTop: "none",
              borderLeft: "none",
              borderRight: "none",
              backgroundImage: `url(${registerIcon})`,
              backgroundRepeat: "no-repeat",
              paddingLeft: "20px",

              cursor: "pointer",
            }}
          />

          <Link
            key={link.id}
            id={link.id}
            fontSize={1}
            px={1}
            sx={{
              fontWeight: "bold",
              display: "inline-block",
              cursor: "pointer",
            
            }}
          >
            {"Register"}
          </Link>
        </div>
      </div>
    );
  }
};

DropMenuContent.propTypes = {
  isUser: PropTypes.bool.isRequired,
};

export default DropMenuContent;
