import preset from "@rebass/preset";

export default {
  ...preset,
  fonts: {
    ...preset.fonts,
    body: `Arial, Helvetica, sans-serif`,
    heading: `Arial, Helvetica, sans-serif`,
    color: `bluie`
  },
};
