import React from "react";
import PropTypes from "prop-types";

import { Link } from "rebass";
import { Input } from "@rebass/forms";

import registerIcon from "../../assets/icons/person_add-black-18dp.svg";
import loginIcon from "../../assets/icons/login-black-18dp.svg";

const DropMenuContent = ({ isUser }) => {
  if (isUser) {
    return (
      <div
        style={{
          display: "flex",
          border: "1px solid red",
          textAlign: "center",
          color: "black",
          flexDirection: "column",
        }}
      >
        <a>Link 1</a>
        <a>Link 2</a>
      </div>
    );
  } else {
    return (
      <div
        style={{
          display: "flex",
          textAlign: "center",
          flexDirection: "column",
          color: "black",
        }}
      >
        <div
          style={{
            display: "inline-block",
            cursor: "pointer",
            padding: "2px 8px",
          }}
        >
          <Input
            id="searchBox"
            placeholder="Register"
            sx={{
              textAlign: "left",
              width: "100%",
              padding: "0",
              textDecoration: "none",
              outline: "none",
              borderBottom: "none",
              borderTop: "none",
              borderLeft: "none",
              borderRight: "none",
              backgroundImage: `url(${registerIcon})`,
              backgroundRepeat: "no-repeat",
              paddingLeft: "24px",
              display: "inline-block",
              border: "1px solid red",
              ":hover":{
                cursor: "pointer"
              },
              "::placeholder": {
                color: "black",
                opacity: "1",
                fontSize: "15px",
              },
            }}
          />
        </div>

        <div
          style={{
            display: "inline-block",
            cursor: "pointer",
            padding: "2px 8px",
          }}
        >
          <Input
            id="searchBox"
            placeholder="Login"
            sx={{
              textAlign: "left",
              width: "100%",
              padding: "0",
              textDecoration: "none",
              outline: "none",
              borderBottom: "none",
              borderTop: "none",
              borderLeft: "none",
              borderRight: "none",
              backgroundImage: `url(${loginIcon})`,
              backgroundRepeat: "no-repeat",
              paddingLeft: "24px",
              display: "inline-block",
              border: "1px solid red",
              ":hover":{
                cursor: "pointer"
              },
              "::placeholder": {
                color: "black",
                opacity: "1",
                fontSize: "15px",
                cursor: "pointer"
              },
            }}
          />
        </div>
      </div>
    );
  }
};

DropMenuContent.propTypes = {
  isUser: PropTypes.bool.isRequired,
};

export default DropMenuContent;
