import React from "react";
import { Card, Image } from "rebass";
import cardImage from "../../assets/images/card-image.jpg"

const GridContent = ({ display }) => {

    if (display === "LAPTOP") {
        return (
            <div
                style={{
                    border: '1px solid red',
                    display: "grid",
                    gridGap: "2vw",
                    gridTemplateColumns: "1fr 1fr 1fr"
                }}
            >

                <Card
                    sx={{
                        border: "1px solid blue",
                        ":hover": {
                            cursor: "pointer",
                        },
                        width: "100%"
                    }}>
                    <Image src={cardImage} />

                </Card>
                <Card
                    sx={{
                        border: "1px solid blue",
                        ":hover": {
                            cursor: "pointer",
                        },
                        width: "100%"
                    }}>
                    <Image src={cardImage} />

                </Card>
                <Card
                    sx={{
                        border: "1px solid blue",
                        ":hover": {
                            cursor: "pointer",
                        },
                        width: "100%"
                    }}>
                    <Image src={cardImage} />

                </Card>
                <div style={{ border: "1px solid blue", textAlign: "center" }}>
                    1
                </div>
                <div style={{ border: "1px solid blue", textAlign: "center" }}>
                    1
                </div>
                <div style={{ border: "1px solid blue", textAlign: "center" }}>
                    1
                </div>
                <div style={{ border: "1px solid blue", textAlign: "center" }}>
                    1
                </div>
                <div style={{ border: "1px solid blue", textAlign: "center" }}>
                    1
                </div>
                <div style={{ border: "1px solid blue", textAlign: "center" }}>
                    1
                </div>
            </div>
        )
    }
    if (display === "MOBILE") {
        return (
            <div
                style={{
                    border: '1px solid red',
                    display: "grid",
                    gridGap: "20px",
                    gridTemplateColumns: "auto"
                }}
            >

                <div style={{ border: "1px solid blue", textAlign: "center" }}>
                    1
                </div>
                <div style={{ border: "1px solid blue", textAlign: "center" }}>
                    1
                </div>
                <div style={{ border: "1px solid blue", textAlign: "center" }}>
                    1
                </div>
                <div style={{ border: "1px solid blue", textAlign: "center" }}>
                    1
                </div>
                <div style={{ border: "1px solid blue", textAlign: "center" }}>
                    1
                </div>
                <div style={{ border: "1px solid blue", textAlign: "center" }}>
                    1
                </div>
                <div style={{ border: "1px solid blue", textAlign: "center" }}>
                    1
                </div>
                <div style={{ border: "1px solid blue", textAlign: "center" }}>
                    1
                </div>
                <div style={{ border: "1px solid blue", textAlign: "center" }}>
                    1
                </div>
            </div>
        )
    }
    return (null);
};



export default GridContent;
