import React, {useState} from "react";
import PropTypes from "prop-types";
import { Flex, Link } from "rebass";

const MenuTabs = ({ activeTab, handleActiveTab }) => {
    const [isRegisterActive, setIsRegisterActive] = useState("2px solid transparent")
    const [isLoginActive, setIsLoginActive] = useState("2px solid transparent")
    if(activeTab === "Register"){
        setIsRegisterActive("2px solid Black")
    }
    if(activeTab === "Login"){
        setIsLoginActive("2px solid Black")
    }
  return (
    <Flex flexDirection="row" justifyContent="space-evenly" pt={3} sx={{}}>
      <Link
        onClick={() => {
          handleActiveTab("Register");
        }}
        sx={{
          fontSize: "20px",
          fontWeight: "Bold",
          padding: "1px 6px",
          borderBottom: `${isRegisterActive}`,
          ":hover": {
            cursor: "pointer",
            borderBottom: "2px solid black",
          },
        }}
      >
        {"Register"}
      </Link>

      <Link
        onClick={() => {
          handleActiveTab("Login");
        }}
        sx={{
          fontSize: "20px",
          fontWeight: "Bold",
          padding: "1px 6px",
          borderBottom: `${isLoginActive}`,
          ":hover": {
            cursor: "pointer",
            borderBottom: "2px solid black",
          },
        }}
      >
        {"Login"}
      </Link>
    </Flex>
  );
};

MenuTabs.propTypes = {
  handleActiveTab: PropTypes.string.isRequired,
  handleActiveTab: PropTypes.func.isRequired,
};

export default MenuTabs;
