import React ,{useState} from "react";
import { Flex } from "rebass";
import PropTypes from "prop-types";
import MenuContent from "./MenuContent";
import { useMediaQuery } from "react-responsive";

const MenuContainer = ({ isOpen, menuLinks }) => {

  const [paddingHeader, setPaddingHeader] = useState(null);

  const handlePaddingHeader = (matches) => {
    if(matches){
      setPaddingHeader(4)
    }
    setPaddingHeader(null)
  }
  const isMobile = useMediaQuery(
    {maxDeviceWidth: 768},
    undefined,
    handlePaddingHeader);

  if (isOpen) {
    return (
      <div>
        {isMobile && (
          <>
            <Flex 
                flexDirection="column"
                px={4}
                sx={{
                    width: "100%"
                }}>
              <MenuContent menuLinks={menuLinks} />
            </Flex>
          </>
        )}
      </div>
    );
  }
  return null;
};

MenuContainer.propTypes = {
  isOpen: PropTypes.bool.isRequired,
  menuLinks: PropTypes.object.isRequired,
};
export default MenuContainer;
