import React from "react";
import PropTypes from "prop-types";
import { Input } from "@rebass/forms";
import registerIconBlack from "../../assets/icons/person_add-black-18dp.svg";
import loginIconBlack from "../../assets/icons/login-black-18dp.svg";
import registerIconWhite from "../../assets/icons/person_add-white-18dp.svg";
import loginIconWhite from "../../assets/icons/login-white-18dp.svg";

const DropMenuContent = ({ isUser }) => {
  if (isUser) {
    return (
      <div
        style={{
          display: "flex",
          border: "1px solid red",
          textAlign: "center",
          color: "black",
          flexDirection: "column",
        }}
      >
        <a>Link 1</a>
        <a>Link 2</a>
      </div>
    );
  } else {
    return (
      <div
        style={{
          display: "flex",
          textAlign: "center",
          flexDirection: "column",
          color: "black",
          paddingTop: "4px",
          paddingBottom: "4px",
        }}
      >
        <div
          style={{
            display: "inline-block",
            cursor: "pointer",
            padding: "3px 8px",
          }}
        >
          <Input
            id="searchBox"
            placeholder="Register"
            sx={{
              color: "transparent",
              textAlign: "left",
              width: "100%",
              padding: "0",
              textDecoration: "none",
              outline: "none",
              borderBottom: "1px solid transparent",
              borderTop: "none",
              borderLeft: "none",
              borderRight: "none",
              backgroundImage: `url(${registerIconBlack})`,
              backgroundRepeat: "no-repeat",
              paddingLeft: "24px",
              display: "inline-block",
              borderRadius: "10px",
              ":hover": {
                cursor: "pointer",
                background: "black",
                backgroundImage: `url(${registerIconWhite})`,
                backgroundRepeat: "no-repeat",
                "::placeholder":{
                  color:"white"
                }
              },
              "::placeholder": {
                color: "black",
                opacity: "1",
                fontSize: "15px",
              },
            }}
          />
        </div>

        <div
          style={{
            display: "inline-block",
            cursor: "pointer",
            padding: "3px 8px",
          }}
        >
          <Input
            id="searchBox"
            placeholder="Login"
            sx={{
              color: "transparent",
              textAlign: "left",
              width: "100%",
              padding: "0",
              textDecoration: "none",
              outline: "none",
              borderBottom: "1px solid transparent",
              borderTop: "none",
              borderLeft: "none",
              borderRight: "none",
              backgroundImage: `url(${loginIconBlack})`,
              backgroundRepeat: "no-repeat",
              paddingLeft: "24px",
              display: "inline-block",
              borderRadius: "10px",
              ":hover": {
                cursor: "pointer",
                background: "black",
                backgroundImage: `url(${loginIconWhite})`,
                backgroundRepeat: "no-repeat",
                "::placeholder":{
                  color:"white",
                },
              },
              "::placeholder": {
                color: "black",
                opacity: "1",
                fontSize: "15px",
                cursor: "pointer",
              },
            }}
          />
        </div>
      </div>
    );
  }
};

DropMenuContent.propTypes = {
  isUser: PropTypes.bool.isRequired,
};

export default DropMenuContent;
