import preset from "@rebass/preset";

export default {
  ...preset,
  fonts: {
    ...preset.fonts,
    body: `Arial, Helvetica, sans-serif`,
    heading: `Arial, Helvetica, sans-serif`,
    fontFamily: `"Gill Sans", "Gill Sans MT", Calibri, "Trebuchet MS", sans-serif`
  },
};
