import React, { useState, Fragment } from "react";
import PropTypes from "prop-types";
import { Flex, Heading, Box, Link } from "rebass";
import { Input, Label} from "@rebass/forms"
import { loggedOutLinks, loggedInLinks } from "./links";
import { useMediaQuery } from "react-responsive";
import MenuContainer from "../../components/Menu/MenuContainer";

const Header = ({ isUser }) => {
  const menuLinks = isUser ? loggedInLinks : loggedOutLinks;
  const [isOpen, setIsOpen] = useState(false);
  const [paddingHeader, setPaddingHeader] = useState(4);

  const handlePaddingHeader = (matches) => {
    if (matches) {
      setPaddingHeader(6);
    }
    setPaddingHeader(4);
  };

  const isMobile = useMediaQuery({ maxDeviceWidth: 768 });
  const isLaptop = useMediaQuery(
    { minDeviceWidth: 769 },
    undefined,
    handlePaddingHeader
  );

  return (
    <div>
      <Flex
        px={paddingHeader}
        py={3}
        color="white"
        bg="black"
        alignItems="center"
      >
        <Heading fontSize={3} fontWeight="bold">
          Home
        </Heading>

        <Box mx="auto" />

        {isLaptop && (
          <>
            {menuLinks.right.map((link) => (
              <Link
                key={link.id}
                id={link.id}
                fontSize={2}
                px={3}
                sx={{
                  cursor: "pointer",
                  borderBottom: "2px solid transparent",
                  ":hover": {
                    borderBottom: "2px solid white",
                  },
                }}
              >
                {link.id}
              </Link>
            ))}
            <div style={{width:"100%"}}>
            <Input
                id="searchBox"
                placeholder="Search..."
                fontSize={1}
                sx={{
                  outline: "none",
                  width: "10%",
                  margin: "0px",
                  padding: "1px",
                  borderBottom: "2px solid white",
                  borderTop: "none",
                  borderLeft: "none",
                  borderRight: "none"
                }}
              />
            </div>
          </>
        )}

        {isMobile && (
          <>
            <Box
              sx={{ cursor: "pointer" }}
              onClick={() => {
                setIsOpen(!isOpen);
              }}
            >
              {isOpen ? (
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="24"
                  height="24"
                  viewBox="0 0 18 18"
                  fill="white"
                >
                  <path d="M14.53 4.53l-1.06-1.06L9 7.94 4.53 3.47 3.47 4.53 7.94 9l-4.47 4.47 1.06 1.06L9 10.06l4.47 4.47 1.06-1.06L10.06 9z" />
                </svg>
              ) : (
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="24"
                  height="24"
                  viewBox="0 0 18 18"
                  fill="white"
                >
                  <path d="M2 13.5h14V12H2v1.5zm0-4h14V8H2v1.5zM2 4v1.5h14V4H2z" />
                </svg>
              )}
            </Box>
          </>
        )}
      </Flex>
      <MenuContainer isOpen={isOpen} menuLinks={menuLinks} />
    </div>
  );
};

Header.propTypes = {
  isUser: PropTypes.bool.isRequired,
};

export default Header;
