import React, { useState, Fragment } from "react";
import PropTypes from "prop-types";
import { Flex, Heading, Box, Link } from "rebass";
import { Input, Label} from "@rebass/forms"
import { loggedOutLinks, loggedInLinks } from "./links";
import { useMediaQuery } from "react-responsive";
import MenuContainer from "../../components/Menu/MenuContainer";

const Header = ({ isUser }) => {
  const menuLinks = isUser ? loggedInLinks : loggedOutLinks;
  const [isOpen, setIsOpen] = useState(false);
  const [paddingHeader, setPaddingHeader] = useState(4);

  const handlePaddingHeader = (matches) => {
    if (matches) {
      setPaddingHeader(6);
    }
    setPaddingHeader(4);
  };

  const isMobile = useMediaQuery({ maxDeviceWidth: 768 });
  const isLaptop = useMediaQuery(
    { minDeviceWidth: 769 },
    undefined,
    handlePaddingHeader
  );

  return (
    <div>
      <Flex
        px={paddingHeader}
        py={3}
        color="white"
        bg="black"
        alignItems="center"
      >
        <Heading fontSize={3} fontWeight="bold">
          Home
        </Heading>

        <Box mx="auto" />

        {isLaptop && (
          <>
            {menuLinks.right.map((link) => (
              <Link
                key={link.id}
                id={link.id}
                fontSize={2}
                px={3}
                sx={{
                  cursor: "pointer",
                  borderBottom: "2px solid transparent",
                  ":hover": {
                    borderBottom: "2px solid white",
                  },
                }}
              >
                {link.id}
              </Link>
            ))}
            <div style={{width:"10%"}}>
              <i><svg class="svg-icon" viewBox="0 0 20 20">
							<path d="M17.659,3.681H8.468c-0.211,0-0.383,0.172-0.383,0.383v2.681H2.341c-0.21,0-0.383,0.172-0.383,0.383v6.126c0,0.211,0.172,0.383,0.383,0.383h1.532v2.298c0,0.566,0.554,0.368,0.653,0.27l2.569-2.567h4.437c0.21,0,0.383-0.172,0.383-0.383v-2.681h1.013l2.546,2.567c0.242,0.249,0.652,0.065,0.652-0.27v-2.298h1.533c0.211,0,0.383-0.172,0.383-0.382V4.063C18.042,3.853,17.87,3.681,17.659,3.681 M11.148,12.87H6.937c-0.102,0-0.199,0.04-0.27,0.113l-2.028,2.025v-1.756c0-0.211-0.172-0.383-0.383-0.383H2.724V7.51h5.361v2.68c0,0.21,0.172,0.382,0.383,0.382h2.68V12.87z M17.276,9.807h-1.533c-0.211,0-0.383,0.172-0.383,0.383v1.755L13.356,9.92c-0.07-0.073-0.169-0.113-0.27-0.113H8.851v-5.36h8.425V9.807z"></path>
						</svg></i>
            <Input
                id="searchBox"
                placeholder="Search..."
                fontSize={1}
                sx={{
                  outline: "none",
                  width: "100%",
                  margin: "0px",
                  padding: "1px",
                  borderBottom: "2px solid white",
                  borderTop: "none",
                  borderLeft: "none",
                  borderRight: "none"
                }}
              />
            </div>
          </>
        )}

        {isMobile && (
          <>
            <Box
              sx={{ cursor: "pointer" }}
              onClick={() => {
                setIsOpen(!isOpen);
              }}
            >
              {isOpen ? (
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="24"
                  height="24"
                  viewBox="0 0 18 18"
                  fill="white"
                >
                  <path d="M14.53 4.53l-1.06-1.06L9 7.94 4.53 3.47 3.47 4.53 7.94 9l-4.47 4.47 1.06 1.06L9 10.06l4.47 4.47 1.06-1.06L10.06 9z" />
                </svg>
              ) : (
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="24"
                  height="24"
                  viewBox="0 0 18 18"
                  fill="white"
                >
                  <path d="M2 13.5h14V12H2v1.5zm0-4h14V8H2v1.5zM2 4v1.5h14V4H2z" />
                </svg>
              )}
            </Box>
          </>
        )}
      </Flex>
      <MenuContainer isOpen={isOpen} menuLinks={menuLinks} />
    </div>
  );
};

Header.propTypes = {
  isUser: PropTypes.bool.isRequired,
};

export default Header;
