import React from "react";
import { Card, Image } from "rebass";
import cardImage from "../../assets/images/card-image.jpg"
import { GridData } from "./GridData";

const GridContent = ({ display }) => {
    const data = GridData.dataArray

    if (display === "LAPTOP") {
        return (
            <div
                style={{
                    border: '1px solid red',
                    display: "grid",
                    gridGap: "2vw",
                    gridTemplateColumns: "1fr 1fr 1fr"
                }}
            >
                {
                    data.map((data, index) => {
                        <Card
                            key={index}
                            sx={{
                                border: "1px solid blue",
                                borderRadius: "5px",
                                ":hover": {
                                    cursor: "pointer",
                                },
                                width: "100%"
                            }}>

                            <Image src={cardImage} />
                            <p>{data.title}</p>
                        </Card>
                    })
                }

                <Card
                    sx={{
                        border: "1px solid blue",
                        borderRadius: "5px",
                        ":hover": {
                            cursor: "pointer",
                        },
                        width: "100%"
                    }}>
                    <Image src={cardImage} />

                </Card>
                <Card
                    sx={{
                        border: "1px solid blue",
                        ":hover": {
                            cursor: "pointer",
                        },
                        width: "100%"
                    }}>
                    <Image src={cardImage} />

                </Card>
                <Card
                    sx={{
                        border: "1px solid blue",
                        ":hover": {
                            cursor: "pointer",
                        },
                        width: "100%"
                    }}>
                    <Image src={cardImage} />

                </Card>
                <div style={{ border: "1px solid blue", textAlign: "center" }}>
                    1
                </div>
                <div style={{ border: "1px solid blue", textAlign: "center" }}>
                    1
                </div>
                <div style={{ border: "1px solid blue", textAlign: "center" }}>
                    1
                </div>
                <div style={{ border: "1px solid blue", textAlign: "center" }}>
                    1
                </div>
                <div style={{ border: "1px solid blue", textAlign: "center" }}>
                    1
                </div>
                <div style={{ border: "1px solid blue", textAlign: "center" }}>
                    1
                </div>
            </div>
        )
    }
    if (display === "MOBILE") {
        return (
            <div
                style={{
                    border: '1px solid red',
                    display: "grid",
                    gridGap: "2vw",
                    gridTemplateColumns: "1fr"
                }}
            >

                <div style={{ border: "1px solid blue", textAlign: "center" }}>
                    1
                </div>
                <div style={{ border: "1px solid blue", textAlign: "center" }}>
                    1
                </div>
                <div style={{ border: "1px solid blue", textAlign: "center" }}>
                    1
                </div>
                <div style={{ border: "1px solid blue", textAlign: "center" }}>
                    1
                </div>
                <div style={{ border: "1px solid blue", textAlign: "center" }}>
                    1
                </div>
                <div style={{ border: "1px solid blue", textAlign: "center" }}>
                    1
                </div>
                <div style={{ border: "1px solid blue", textAlign: "center" }}>
                    1
                </div>
                <div style={{ border: "1px solid blue", textAlign: "center" }}>
                    1
                </div>
                <div style={{ border: "1px solid blue", textAlign: "center" }}>
                    1
                </div>
            </div>
        )
    }
    return (null);
};



export default GridContent;
