import React, { useState } from "react";
import PropTypes from "prop-types";
import { Flex, Heading, Box, Link, Button } from "rebass";
import { Input } from "@rebass/forms";
import { loggedOutLinks, loggedInLinks } from "./links";
import { useMediaQuery } from "react-responsive";
import MenuContainer from "../../components/Menu/MenuContainer";
import searchIcon from "../../assets/icons/search-white-18dp.svg";
import theme from "../../components/Layout/theme";
import heroImage from "../../assets/images/hero-image3.jpg";

const Header = ({ isUser }) => {
  const menuLinks = isUser ? loggedInLinks : loggedOutLinks;
  const [isOpen, setIsOpen] = useState(false);
  const [paddingHeader, setPaddingHeader] = useState(4);

  const handlePaddingHeader = (matches) => {
    if (matches) {
      setPaddingHeader(6);
    }
    setPaddingHeader(4);
  };

  const isMobile = useMediaQuery({ maxDeviceWidth: 768 });
  const isLaptop = useMediaQuery(
    { minDeviceWidth: 769 },
    undefined,
    handlePaddingHeader
  );

  return (
    <div>
      <Flex
        px={paddingHeader}
        py={3}
        color="white"
        alignItems="flex-start"
        height={500}
        sx={{
          overflow: "hidden",
          width: "100%",
          backgroundImage: `linear-gradient(rgba(0,0,0,0.65),rgba(0,0,0,0.65)),url(${heroImage})`,
          backgroundAttachment: "scroll",
          backgroundRepeat: "no-repeat",
          backgroundSize: "cover",
          backgroundPosition: "center",
        }}
      >
        <Heading
          fontFamily={theme.fonts.fontFamily}
          fontSize={3}
          fontWeight="bold"
        >
          Home
        </Heading>

        <Box mx="auto" />

        {isLaptop && (
          <>
            <svg
              width="20px"
              height="16px"
              viewBox="0 0 20 16"
              version="1.1"
              xmlns="http://www.w3.org/2000/svg"
              xmlns="http://www.w3.org/1999/xlink"
            >
                     <path
                      d="M10.29,7377 C17.837,7377 21.965,7370.84365 21.965,7365.50546 C21.965,7365.33021 21.965,7365.15595 21.953,7364.98267 C22.756,7364.41163 23.449,7363.70276 24,7362.8915 C23.252,7363.21837 22.457,7363.433 21.644,7363.52751 C22.5,7363.02244 23.141,7362.2289 23.448,7361.2926 C22.642,7361.76321 21.761,7362.095 20.842,7362.27321 C19.288,7360.64674 16.689,7360.56798 15.036,7362.09796 C13.971,7363.08447 13.518,7364.55538 13.849,7365.95835 C10.55,7365.79492 7.476,7364.261 5.392,7361.73762 C4.303,7363.58363 4.86,7365.94457 6.663,7367.12996 C6.01,7367.11125 5.371,7366.93797 4.8,7366.62489 L4.8,7366.67608 C4.801,7368.5989 6.178,7370.2549 8.092,7370.63591 C7.488,7370.79836 6.854,7370.82199 6.24,7370.70483 C6.777,7372.35099 8.318,7373.47829 10.073,7373.51078 C8.62,7374.63513 6.825,7375.24554 4.977,7375.24358 C4.651,7375.24259 4.325,7375.22388 4,7375.18549 C5.877,7376.37088 8.06,7377 10.29,7376.99705"
                      id="twitter-[#154]"
                    ></path>
            </svg>

            {menuLinks.right.map((link) => (
              <Link
                key={link.id}
                id={link.id}
                fontSize={2}
                px={3}
                sx={{
                  cursor: "pointer",
                  borderBottom: "2px solid transparent",
                  ":hover": {
                    borderBottom: "2px solid white",
                  },
                }}
              >
                {link.id}
              </Link>
            ))}
            <div style={{ width: "10%", paddingLeft: "1vw" }}>
              <Input
                id="searchBox"
                placeholder="Search..."
                fontSize={1}
                sx={{
                  outline: "none",
                  width: "100%",
                  padding: "1px",
                  borderBottom: "2px solid white",
                  borderTop: "none",
                  borderLeft: "none",
                  borderRight: "none",
                  backgroundImage: `url(${searchIcon})`,
                  backgroundRepeat: "no-repeat",
                  paddingLeft: "20px",
                }}
              />
            </div>
          </>
        )}

        {isMobile && (
          <>
            <Box
              sx={{ cursor: "pointer" }}
              onClick={() => {
                setIsOpen(!isOpen);
              }}
            >
              {isOpen ? (
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="24"
                  height="24"
                  viewBox="0 0 18 18"
                  fill="white"
                >
                  <path d="M14.53 4.53l-1.06-1.06L9 7.94 4.53 3.47 3.47 4.53 7.94 9l-4.47 4.47 1.06 1.06L9 10.06l4.47 4.47 1.06-1.06L10.06 9z" />
                </svg>
              ) : (
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="24"
                  height="24"
                  viewBox="0 0 18 18"
                  fill="white"
                >
                  <path d="M2 13.5h14V12H2v1.5zm0-4h14V8H2v1.5zM2 4v1.5h14V4H2z" />
                </svg>
              )}
            </Box>
          </>
        )}
      </Flex>
      <MenuContainer isOpen={isOpen} menuLinks={menuLinks} />
    </div>
  );
};

Header.propTypes = {
  isUser: PropTypes.bool.isRequired,
};

export default Header;
