import React, { useState, Fragment } from "react";
import PropTypes from "prop-types";
import { Flex, Heading, Box, Link, Text } from "rebass";
import { loggedOutLinks, loggedInLinks } from "./links";
import MenuContainer from "../../components/Menu/MenuContainer";
import Media from "react-media";

const Header = ({ isUser }) => {
  const links = isUser ? loggedInLinks : loggedOutLinks;
  const [isOpen, setIsOpen] = useState(false);
  const [paddingHeader, setPaddingHeader] = useState();

  return (
    <div>
      <Flex
        alignItems="center"
        px={paddingHeader}
        py={3}
        sx={{
          boxShadow: "0 2px 4px 0 rgba(0,0,0,.1)",
          height: "70px",
          background: "#f13c20",
          borderBottom: "2px solid white",
          position: "fixed",
          top: 0,
          width: "100%",
        }}
      >
        <Heading>
          <Text
            fontSize={4}
            fontWeight="900"
            color="white"
            sx={{
              cursor: "pointer",
            }}
          >
            Home
          </Text>
        </Heading>

        <Box mx="auto" />
        <div>
          <Media
            queries={{
              small: "(max-width: 768px)",
              large: "(min-width: 769px)",
            }}
          >
            {(matches) => (
              <Fragment>
                {matches.small && (
                  <Box
                    sx={{
                      cursor: "pointer",
                    }}
                    onClick={() => {
                      setIsOpen(!isOpen);
                    }}
                  >
                    {setPaddingHeader(4)}
                    {isOpen ? (
                      <svg
                        xmlns="http://www.w3.org/2000/svg"
                        width="24"
                        height="24"
                        viewBox="0 0 18 18"
                        fill="white"
                      >
                        <path d="M14.53 4.53l-1.06-1.06L9 7.94 4.53 3.47 3.47 4.53 7.94 9l-4.47 4.47 1.06 1.06L9 10.06l4.47 4.47 1.06-1.06L10.06 9z" />
                      </svg>
                    ) : (
                      <svg
                        xmlns="http://www.w3.org/2000/svg"
                        width="24"
                        height="24"
                        viewBox="0 0 18 18"
                        fill="white"
                      >
                        <path d="M2 13.5h14V12H2v1.5zm0-4h14V8H2v1.5zM2 4v1.5h14V4H2z" />
                      </svg>
                    )}
                  </Box>
                )}
                {matches.large && (
                  <>
                    {setPaddingHeader(6)}
                    {links.right.map((link) => (
                      <Link
                        key={link.id}
                        px={4}
                        fontSize={2}
                        color="white"
                        sx={{
                          cursor: "pointer",
                          borderBottom: "2px solid transparent",
                          ":hover": {
                            color: "white",
                            borderBottom: "2px solid white",
                          },
                        }}
                        variant="nav"
                      >
                        {link.id}
                      </Link>
                    ))}
                  </>
                )}
              </Fragment>
            )}
          </Media>
        </div>
      </Flex>
      <MenuContainer isOpen={isOpen} links={links} />
    </div>
  );
};

Header.propTypes = {
  isUser: PropTypes.bool.isRequired,
};

export default Header;
