import React, { useState } from "react";
import PropTypes from "prop-types";
import { Flex, Heading, Box, Link, Button } from "rebass";
import { Input } from "@rebass/forms";
import { loggedOutLinks, loggedInLinks } from "./links";
import { useMediaQuery } from "react-responsive";
import MenuContainer from "../../components/Menu/MenuContainer";
import searchIcon from "../../assets/search-white-18dp.svg";
import theme from "../../components/Layout/theme";
import heroImage from  "../../assets/hero-image.jpg"

const Header = ({ isUser }) => {
  const menuLinks = isUser ? loggedInLinks : loggedOutLinks;
  const [isOpen, setIsOpen] = useState(false);
  const [paddingHeader, setPaddingHeader] = useState(4);
  const [heroImage, setHeroImage] = useState(        {
    overflow: "hidden",
    width: "100%",
    backgroundImage: `linear-gradient(rgba(0,0,0,0.65),rgba(0,0,0,0.65)),url(${heroImage})`,
    backgroundAttachment: "scroll",
    backgroundRepeat: "no-repeat",
    backgroundSize: "cover",
    backgroundPosition: "center",
  });

  const handlePaddingHeader = (matches) => {
    if (matches) {
      setPaddingHeader(6);
      setHeroImage(
        {
          overflow: "hidden",
          width: "100%",
          backgroundImage: `linear-gradient(rgba(0,0,0,0.65),rgba(0,0,0,0.65)),url(${heroImage})`,
          backgroundAttachment: "scroll",
          backgroundRepeat: "no-repeat",
          backgroundSize: "cover",
          backgroundPosition: "center",
        }
      )
    }
    setPaddingHeader(4);
  };

  const isMobile = useMediaQuery({ maxDeviceWidth: 768 });
  const isLaptop = useMediaQuery(
    { minDeviceWidth: 769 },
    undefined,
    handlePaddingHeader
  );

  return (
    <div>
      <Flex
        px={paddingHeader}
        py={3}
        color="white"
        alignItems="flex-start"
        height = {500}
        sx= {heroImage}
      >
        <Heading fontFamily={theme.fonts.fontFamily} fontSize={3} fontWeight="bold">
          Home
        </Heading>

        <Box mx="auto" />

        {isLaptop && (
          <>
            {menuLinks.right.map((link) => (
              <Link
                key={link.id}
                id={link.id}
                fontSize={2}
                px={3}
                sx={{
                  cursor: "pointer",
                  borderBottom: "2px solid transparent",
                  ":hover": {
                    borderBottom: "2px solid white",
                  },
                }}
              >
                {link.id}
              </Link>
            ))}
            <div style={{ width: "10%", paddingLeft: "1vw" }}>
              <Input
                id="searchBox"
                placeholder="Search..."
                fontSize={1}
                sx={{
                  outline: "none",
                  width: "100%",
                  padding: "1px",
                  borderBottom: "2px solid white",
                  borderTop: "none",
                  borderLeft: "none",
                  borderRight: "none",
                  backgroundImage: `url(${searchIcon})`,
                  backgroundRepeat: "no-repeat",
                  paddingLeft: "20px",
                }}
              />
            </div>
          </>
        )}

        {isMobile && (
          <>
            <Box
              sx={{ cursor: "pointer" }}
              onClick={() => {
                setIsOpen(!isOpen);
              }}
            >
              {isOpen ? (
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="24"
                  height="24"
                  viewBox="0 0 18 18"
                  fill="white"
                >
                  <path d="M14.53 4.53l-1.06-1.06L9 7.94 4.53 3.47 3.47 4.53 7.94 9l-4.47 4.47 1.06 1.06L9 10.06l4.47 4.47 1.06-1.06L10.06 9z" />
                </svg>
              ) : (
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="24"
                  height="24"
                  viewBox="0 0 18 18"
                  fill="white"
                >
                  <path d="M2 13.5h14V12H2v1.5zm0-4h14V8H2v1.5zM2 4v1.5h14V4H2z" />
                </svg>
              )}
            </Box>
          </>
        )}
      </Flex>
      <MenuContainer isOpen={isOpen} menuLinks={menuLinks} />
    </div>
  );
};

Header.propTypes = {
  isUser: PropTypes.bool.isRequired,
};

export default Header;
